import React, { useEffect } from 'react'
import { useForm } from 'react-hook-form'
import { Link } from 'react-router-dom'
import RadioGroup from '@material-ui/core/FormGroup'
import Radio from '@material-ui/core/Radio'
import ListItem from '@material-ui/core/ListItem'
import FormControl from '@material-ui/core/FormControl'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import FormLabel from '@material-ui/core/FormLabel'

import * as Survey from 'survey-react'
import 'survey-react/survey.css'

const responseURL = 'http://localhost:3000/question_responses'

function SurveyPage({ openSurvey }) {

    const { register, handleSubmit, getValues } = useForm()


    const json = {
        title: `${openSurvey.title}`,
        pages: [
            {
                name: 'Page 1', elements: [
                    {type: 'panel', name: `${openSurvey.id}`, elements: []}
                ]
            }
        ]
    }

    const model = new Survey.Model(json)

    useEffect(() => {
        const survey = model.getPanelByName(`${openSurvey.id}`)
        openSurvey.survey_questions.forEach(question => {
            survey.addQuestion({
                name: `${question.id}`,
                title: `${question.content}`,
                isRequired: true,
                type: 'radiogroup'
            })
            console.log('question displayed?', question)
        })
    })

    const submitAnswers = (event) => {
        event.preventDefault()
        const answers = getValues()
        console.log(answers)
        const confirmation = fetch(responseURL, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(answers)
        })
    }


    return (
        <div>
            {/* <h2>{openSurvey.title}</h2> */}
            {/* <form className='questions-list'
                onSubmit={submitAnswers}>

                {displayQuestions()}

                {/* <ol>
                    {displayQuestions()}
                </ol> */}
                {/* <button type='submit'>Submit</button>
            </form> */} */}
            <Survey.Survey model={survey} onComplete={submitAnswers} />
            <br/>
            <Link to='/'><button>Cancel</button></Link>
        </div>
    )
}

export default SurveyPage
