import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import * as Survey from 'survey-react'
import 'survey-react/survey.css'

const responseURL = 'http://localhost:3000/question_responses'

function SurveyPage({ openSurvey, setOpenSurvey }) {

    const { survey_questions } = openSurvey

    const json = {
        title: `${openSurvey.title}`,
        pages: [
            { name: 'Page 1', elements: [
                { name: `${openSurvey.id}`, elements: [] }
            ] }
        ]
    }

    const survey = new Survey.Model(json)
    const panel = survey.getPanelByName(`${openSurvey.id}`)

    useEffect(() => {


            console.log('panel', panel)
            console.log('useEffect hits')
            console.log('questions', survey_questions)
            survey_questions.forEach(question => {
                console.log('uE loop hits')
                panel.addNewQuestion({
                    name: `${question.id}`,
                    title: `${question.content}`,
                    isRequired: true,
                    questionType: 'radiogroup'
                })
            })
    })

    const collectAnswers = () => {
        return survey_questions.map(question => {
            return survey.getValue(`${question.id}`)
        })
    }

    const submitAnswers = (event) => {
        event.preventDefault()

        console.log('collected answers', collectAnswers())

        fetch(responseURL, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(collectAnswers())
        })
        setOpenSurvey({})
    }


    return (
        <div>
            <Survey.Survey
                model={survey}
                onComplete={submitAnswers}
                questionsOnPageMode='singlePage'
                mode='display'
            />
            <br/>
            <Link to='/'><button>Cancel</button></Link>
        </div>
    )
}

export default SurveyPage
