import React, { useEffect } from 'react'
import { useForm } from 'react-hook-form'
import { Link } from 'react-router-dom'
import RadioGroup from '@material-ui/core/FormGroup'
import Radio from '@material-ui/core/Radio'
import ListItem from '@material-ui/core/ListItem'
import FormControl from '@material-ui/core/FormControl'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import FormLabel from '@material-ui/core/FormLabel'

import * as Survey from 'survey-react'
import 'survey-react/survey.css'

const responseURL = 'http://localhost:3000/question_responses'

function SurveyPage({ openSurvey }) {

    const { register, handleSubmit, getValues } = useForm()

    const { survey_questions } = openSurvey

    const json = {
        title: `${openSurvey.title}`,
        pages: [{ name: `${openSurvey.id}`, elements: [] }]
    }

    const survey = new Survey.Model(json)
    const page = survey.getPageByName(`${openSurvey.id}`)

    useEffect(() => {
        survey_questions.forEach(question => {
            page.addQuestion({
                name: `${question.id}`,
                title: `${question.content}`,
                isRequired: true,
                type: 'radiogroup'
            })
            console.log('question displayed?', question)
        })
    })

    const submitAnswers = (event) => {
        event.preventDefault()
        const answers = survey_questions.map(question => {
            return survey.getValue(`${question.id}`)
        })
        console.log(answers)
        fetch(responseURL, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(answers)
        })
    }


    return (
        <div>
            <Survey.Survey
                model={survey}
                onComplete={submitAnswers}
                questionsOnPageMode='singlePage'
            />
            <br/>
            <Link to='/'><button>Cancel</button></Link>
        </div>
    )
}

export default SurveyPage
